import React from 'react';
import { Card, Button, CardTitle, CardText } from 'reactstrap';

function Description(props) {
  return (
    <div>
        <Card body>
        <CardTitle><center><p style={{fontWeight:"bold",fontSize:"35px",color:"white",fontFamily:"timeburner"}}>{props.stage.label}</p></center></CardTitle>
        <CardText><p style={{color:"white",fontFamily:"timeburner"}}>{props.stage.description}</p></CardText>
        {/* <Button>Go somewhere</Button> */}
      </Card>
      {/* <center><p style={{fontWeight:"bold",fontSize:"35px",color:"white",fontFamily:"timeburner"}}>{props.stage.label}</p></center>
      <p style={{color:"white",fontFamily:"timeburner"}}>{props.stage.description}</p> */}
    </div>
  );
}

export default Description;
