import React from 'react';
import { Card, Button, CardHeader, CardFooter, CardBody,CardTitle, CardText } from 'reactstrap';
import 'react-perfect-scrollbar/dist/css/styles.css';
import PerfectScrollbar from 'react-perfect-scrollbar';

function Description(props) {
  return (
    <div>
      <center>{props.stage.value ? <p style={{fontWeight:"bold",fontSize:"35px",color:"white",fontFamily:"timeburner"}}>{props.stage.label}</p> : <p style={{fontWeight:"bold",fontSize:"35px",color:"white"}}>{props.stage.labelTel}</p> }</center>
      <PerfectScrollbar>
        <p className="overflow-auto" style={{color:"white",fontFamily:"timeburner"}}>{props.stage.description}</p>
      </PerfectScrollbar>
    </div>
  );
}

export default Description;
