import React from 'react';
import { Card, Button, CardHeader, CardFooter, CardBody,
  CardTitle, CardText } from 'reactstrap';
import ScrollArea from 'react-scrollbar';

function Description(props) {
  return (
    <div>
      <Card style={{maxHeight:"1000px"}}>
        <CardHeader><center><p style={{fontWeight:"bold",fontSize:"35px",color:"blue",fontFamily:"timeburner"}}>{props.stage.label}</p></center></CardHeader>
        <CardBody>
          <CardTitle>Special Title Treatment</CardTitle>
          <CardText><ScrollArea><div><p style={{color:"red",fontFamily:"timeburner"}}>{props.stage.description}</p></div> </ScrollArea></CardText>
          <Button>Go somewhere</Button>
        </CardBody>
        <CardFooter>Footer</CardFooter>
      </Card>
      {/* <center><p style={{fontWeight:"bold",fontSize:"35px",color:"white",fontFamily:"timeburner"}}>{props.stage.label}</p></center>
      <p style={{color:"white",fontFamily:"timeburner"}}>{props.stage.description}</p> */}
    </div>
  );
}

export default Description;
